#!usr/bin/python
# -*- coding: utf-8 -*-

import base64
def to_png(base64_strs,filename):
    base64_strs = base64_strs.split("base64,")[1]
    #filename =  "234.png"

    with open(filename,'wb') as f:
        f.write(base64.b64decode(base64_strs))
    return  filename



if __name__ =="__main__":
    strs = """data:image/png;base64,"""
    to_png(strs,'234.png')